  def genCompanyName(baseName: String): String = {
    new String(
      baseName + "-" +
        LocalDate.now.getYear.toString +
        "%02d".format(LocalDate.now.getMonthValue) +
        "%02d".format(LocalDate.now.getDayOfMonth) +
        "-" + randomString(8)
    )
  }

